# This file is automatically updated at install time.
# Do not edit this file. To customize the Data Science Studio
# environment, edit the bin/env-site.sh file
export DKUINSTALLDIR="/data1/tools/dataiku-dss-2.0.1"
export DKU_BASE_PORT="10000"
export DKU_NGINX_PORT="10000"
export DKU_BACKEND_PORT="10001"
export DKU_IPYTHON_PORT="10002"
export DKU_HPROXY_PORT="10003"
export DKUJAVABIN="/usr/bin/java"
export DKU_JAVA_OPTS="-Dfile.encoding=utf8"
export DKU_BACKEND_JAVA_OPTS="-ea -XX:+UseParallelGC -Xloggc:/dev/stderr -Xms256m -Xmx2G -XX:+PrintGCDetails -XX:+PrintGCTimeStamps -Djava.awt.headless=true -Djava.security.egd=file:/dev/./urandom"
export DKU_HPROXY_JAVA_OPTS="-ea -XX:+UseParallelGC -Xloggc:/dev/stderr -XX:+PrintGCDetails -XX:+PrintGCTimeStamps -XX:MaxPermSize=256m"
export DKU_JEK_JAVA_OPTS="-ea -XX:+UseParallelGC -Xloggc:/dev/stderr -Xmx2G -XX:+PrintGCDetails -XX:+PrintGCTimeStamps"
export DKU_DKU_JAVA_OPTS="-ea -XX:+UseParallelGC -Xloggc:/dev/stderr -Xmx2G"
